"(evt: Event): void => {
    /**
     * Event triggered when button is type submit
     * Event detail.event retrieves the event and detail.target the event.target of the element clicked
     * @event srui-submit-event
     */
    this.dispatchEvent(new CustomEvent('srui-submit-event', {
      composed: true,
      bubbles: true,
      detail: {
        event: evt,
        target: evt.target
      }
    }));
  }"

"(evt: Event, lang: String): void => {
    /**
     * Event triggered when language is selected
     * lang retrieves the selected language
     * @event srui-language-selection
     */
    this.dispatchEvent(new CustomEvent('srui-language-selection', {
      composed: true,
      bubbles: true,
      detail: {
        event: evt,
        lang: lang
      }
    }));
  }"

"(e: any): void => {
    if (!e['srcElement'].classList.value.includes('current-language-button')) {
      if (this.open) {
        this.open = false;
      }
    }
    /**
     * Event triggered to close the selector
     * lang retrieves the selected language
     * @event srui-close-language-selection
     */
    this.dispatchEvent(new CustomEvent('srui-close-language-selection', {
      composed: true,
      bubbles: true,
      detail: {
        event: e
      }
    }));
  }"
